template.value:
respond_to do |format|
    if @$ATTRIBUTE_NAMES$s
      format.html { render :nothing}
      format.json { render :json => {
        data: {
            $ATTRIBUTE_NAMES$s: @$ATTRIBUTE_NAMES$s.as_json,
            current_page: @$ATTRIBUTE_NAMES$s.current_page,
            per_page: @$ATTRIBUTE_NAMES$s.per_page,
            total_entries: @$ATTRIBUTE_NAMES$s.total_entries
        }, status: 200,
        result: 'success'} }
    else
      format.html { render :nothing}
      format.json { render :show}
    end
  end

def as_json(options = nil)
  super(options || {
      :methods =>
          [
              :post_comments_count, :post_upvotes, :post_downvotes, :post_upvoted?, :post_downvoted?
          ], :include =>
          [
              :post_attachment_images, :tags,
              {
                  :user => {
                      :only => [:image, :name
                      ]
                  }
              }, #must include"{}"
              :post_comments => {
                  :include => {
                      :user => {
                          :only => [
                              :image, :name
                          ]
                      }
                  }
              }
          ]
  }
  )
end
